import React, { useState, useEffect } from 'react';
import { config } from '@gluestack-ui/config';
import { GluestackUIProvider, Box, Text, Input, Modal, View } from '@gluestack-ui/themed';

export default function App() {
  return (
    <GluestackUIProvider config={config}>
      <LoginScreen />
    </GluestackUIProvider>
  );
}

const LoginScreen = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Comprobamos si el usuario ya está autenticado
    const userIsLoggedIn = checkIfUserIsLoggedIn();
    setIsLoggedIn(userIsLoggedIn);
  }, []);

  const checkIfUserIsLoggedIn = () => {
    // Aquí puedes implementar la lógica para verificar si el usuario ya ha iniciado sesión
    // Por ejemplo, puedes comprobar si hay un token de autenticación en el almacenamiento local
    // o si el usuario ya tiene una sesión activa en el servidor
    // Por ahora, asumiremos que el usuario no está autenticado
    return false;
  };

  const handleLogin = () => {
    // Aquí puedes agregar la lógica para manejar el inicio de sesión
    console.log('Iniciar sesión con:', email, password);
    // Aquí podrías añadir la lógica para autenticar al usuario y redirigirlo a la página principal
    // Por ejemplo, podrías enviar una solicitud al servidor para verificar las credenciales del usuario
    // y luego redirigirlo a la página principal si las credenciales son válidas.
    // Además, podrías mostrar mensajes de error si las credenciales son inválidas.

    // Por ahora, simularemos que el usuario se ha autenticado correctamente
    setIsLoggedIn(true);
  };

  if (isLoggedIn) {
    // Si el usuario está autenticado, podríamos redirigirlo a la página principal o a cualquier otra página que deseemos mostrar después del inicio de sesión.
    // Por ahora, simplemente devolvemos `null` para no mostrar nada en esta pantalla.
    return null;
  }

  return (
    <Box flex={1} justifyContent="center" alignItems="center" backgroundColor="$black">
      <Modal visible={true} animationType="slide">
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <Box backgroundColor="$black" padding={20} width="80%" borderRadius={10}>
            <Text color="$white" fontSize={24} fontWeight="bold" marginBottom={20}>Iniciar sesión</Text>
            <Input
              placeholder="Correo electrónico"
              value={email}
              onChangeText={setEmail}
              placeholderTextColor="$textLight600"
              backgroundColor="$inputBg"
              color="$white"
              marginBottom={20}
            />
            <Input
              placeholder="Contraseña"
              value={password}
              onChangeText={setPassword}
              placeholderTextColor="$textLight600"
              backgroundColor="$inputBg"
              color="$white"
              secureTextEntry
              marginBottom={20}
            />
            <Button title="Iniciar sesión" onPress={handleLogin} />
          </Box>
        </View>
      </Modal>
    </Box>
  );
};
